import React from 'react';
import './login-style.css';
import { Link } from 'react-router-dom';
import * as routes from './constants/routes.js';
 
const SignUp = () => 
	<div className="container">
		<SignUpForm />
	</div>

const INITIAL_STATE = 
{ 
	email: '', 
	passwordOne: '', 
	passwordTwo: '', 
	error: null, 
};

const byPropKey = (propertyName, value) => () => ({ 
	[propertyName]: value, 
});

class SignUpForm extends React.Component {
	constructor(props) {
	super(props);

	this.state = { ...INITIAL_STATE };

}

onSubmit = (event) => {

}

render() { 
	const { 
		email, passwordOne, 
		passwordTwo, 
		error, } = this.state;

	return ( 
		<form onSubmit={this.onSubmit}>
			<form className="signUp" onSubmit={this.onSubmit}>
				<h3>Create Your Account</h3>
				<p>Just enter your email address<br />
				and your password for join.
				</p>
				<input 
				className="w100"
				type="email"
				value={email}
				onChange={event => this.setState(byPropKey('email', event.target.value))}
			    placeholder="Insert email" 
				required
				autoComplete='off' />
				<input 
				type="password" 
				value={passwordOne}
				onChange={event => this.setState(byPropKey('passwordOne', event.target.value))}
				placeholder="Insert password" 
				required />
				<input 
				type="password" 
				value={passwordTwo}
				onChange={event => this.setState(byPropKey('passwordTwo', event.target.value))}
				placeholder="Repeat password" 
				required />
				<Link to={routes.SIGN_IN}><button className="form-btn sx log-in" type="button">Log In</button></Link>
				<Link to={routes.PET}><button className="form-btn dx" type="submit">Sign Up</button></Link>
			</form>
		</form>
		);
	}
}

const SignUpLink = () => 
<p> Don't have an account? 
	{' '} 
	<Link to={routes.SIGN_UP}>Sign Up</Link>
</p>

export default SignUp;

export { 
	SignUpForm,
	SignUpLink, 
};
