import React, { Component } from 'react';
import SignUp from './SignUp.js';
import './login-style.css';
import './toggleFunctions.js';


class SignIn extends Component {
	constructor(){
  		super()
    	this.state={
      	showMe:true
    	}
  	}
  	operation()
	{
	  this.setState({
	    showMe:!this.state.showMe
	  })
	}

  	render() {
  		return (
  			<div className="container">
				<form className="signUp">
					<h3>Welcome<br />Back to<br />
					Tamagochi !</h3><br />
					<input type="email" placeholder="Insert email" autocomplete='off' reqired />
					<input type="password" placeholder="Insert password" reqired />
					{
						this.state.showMe?
				        <SignIn />
				        :<SignUp />
					}
					<button className="form-btn sx back" type="button" onClick={()=>this.operation()}>Back</button>
					<button className="form-btn dx" type="submit">Log In</button>
				</form>
			</div>
  		);
  	}

}

export default SignIn;
