import React from 'react';
import './login-style.css';
import './toggleFunctions.js';
import { showSignIn } from './toggleFunctions.js';

 
class SignUp extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			email: '',
			password: ''
		}
	}

	onEmailChange = (event) => {
		this.setState({email: event.target.value})
	}

	onPasswordChange = (event) => {
		this.setState({password: event.target.value})
	}

	onSubmitSignIn = (event) => {
		event.preventDefault();

		fetch('http://localhost:3000/signin', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
				email: this.state.signInEmail,
				password: this.state.signInPassword
			})
		})
		.then(response => response.json())
		.then(data => {
			if(data === 'success'){
				this.props.onRouteChange('home');
			}
		})
	}

	render() {
		//const { onRouteChange } = this.props;
		return (
		<div className="container">
			<form className="signUp">
				<h3>Create Your Account</h3>
				<p>Just enter your email address<br />
				and your password for join.
				</p>
				<input 
				className="w100"
				 type="email"
				  placeholder="Insert email" 
				  required autoComplete='off'
				  onChange={this.onEmailChange} />
				<input 
				type="password" 
				placeholder="Insert password" 
				required 
				onChange={this.onPasswordChange} />
				<input type="password" 
				placeholder="Verify password" 
				required 
				onChange={this.onPasswordChange} />
				<button className="form-btn sx log-in" type="button" onClick={ showSignIn }>Log In</button>
				<button className="form-btn dx" type="submit">Sign Up</button>
			</form>
		</div>
		);
	}
}

export default SignUp;
