import React from 'react';
import Dropdown from './Dropdown/Dropdown.js';
import './settingscontainer.css';


class SettingsContainer extends React.Component {
  constructor() {
    super();
    
    this.state = {
      languageB: 'Jp',
      languageS: 'Japanese'
    }
  }

  changeLanguage = (lang) => {
    //-------------------------------------------------
    //
    //here send change of the language that is being taught
    //
    //-------------------------------------------------
    if(lang==='Japanese'){
      this.setState({
        languageB: 'Jp',
        languageS: lang
      });
    } else if(lang==='Korean'){
      this.setState({
        languageB: 'Kr',
        languageS: lang
      });
    } else if(lang==='Polish'){
      this.setState({
        languageB: 'Pl',
        languageS: lang
      });
    }
  }

  render () {
    return (
      <div className="right-half" id="mainWrap">
        <div className="half">
          <div id="settingsWrapper">
            <div className="dropdownWrapper">
              <div className="dropdown">
                <div className="dropdownTitle">New password:</div>
                <div className="dropdownOption">
                  <input type="password" placeholder="Insert new password" required className="pwd" />
                </div>
              </div>
            </div>
            <div className="dropdownWrapper">
              <div className="dropdown">
                <div className="dropdownTitle">Choose category:</div>
                <div className="dropdownOption">
                  <Dropdown />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default SettingsContainer;
