import React from 'react';
import './answerbuttons.css';

class AnswerButtons extends React.Component {
  constructor(props) {
    super(props);
    
    this.state = {
      answer: 'B',
      answerA: '',
      answerB: '',
      answerC: '',
      answerD: '',
      checked: false,
      disabled: false
    }
  }

componentWillMount(){
  this.setState({
    answerA: '魚'
  });
}

changeColor(){
  this.setState({
    checked: !this.state.checked
  });
}

  render () {
    let answer_class;
    if(this.state.answer === 'A'){
      console.log("correct answer is A");
      answer_class = this.state.checked ? "btn-answer correct-answer" : "btn-answer";
    } else {
      answer_class = this.state.checked ? "btn-answer incorrect-answer" : "btn-answer";
    }

    return (
      <div className="buttons-container">
        <div className="btn-row">
          <button className={answer_class} onClick={this.changeColor.bind(this)}>{this.state.answerA}</button>
          <button className="btn-answer">魚</button>
        </div>
        <div className="btn-row">
          <button className="btn-answer">魚</button>
          <button className="btn-answer">魚</button>
        </div>
      </div>
    );
  }
}

export default AnswerButtons;
