import React from 'react';
import './dropdown.css';

class Dropdown extends React.Component {
  constructor() {
    super();
    
    this.state = {
      showDrop: false,
      value: 'select'
    }
    
    this.showDrop = this.showDrop.bind(this);
    this.hideDrop = this.hideDrop.bind(this);
  }
  
  showDrop(event) {
    event.preventDefault();
    
    this.setState({ showDrop: true }, () => {
      document.addEventListener('click', this.hideDrop);
    });
  }
  
  hideDrop(event) {
    this.setState({ showDrop: false }, () => {
      document.removeEventListener('click',     this.hideDrop);
    });
  }

  handleChange = (category) => () => {
    console.log(category);
    this.setState({ value: category, showDrop: false });
  }

  render () {
    return (
      <div className='dropMenu'>
        <button className='drop-btn' onClick={this.showDrop}>{this.state.value}</button>
      
      {
      this.state.showDrop ? (
        <ul className='dropDown'>
          <li className="elem" onClick={this.handleChange("Animals")}>Animals</li>
          <li className="elem">People</li>
          <li className="elem">School</li>
          <li className="elem">House</li>
          <li className="elem">Birthday</li>
        </ul>
        ) : (
          null
        )
      } 
      </div>
    );
  }
}

export default Dropdown;
