import React from 'react';
import './petpic.css';

class PetPic extends React.Component {
  constructor(props) {
    super(props);
    
    this.state = {
      pet: '',
      alive: false,
    }
  }
 
 animate(){
  console.log("animation WOOP WOOP");
 }

 renderAlivePet(){
  return (
    <div>
      <img className="pet-pic" src="https://image.flaticon.com/icons/svg/1089/1089427.svg" onClick={this.animate} alt="" />
    </div>
  );
 }

 renderDeadPet(){
  return (
    <div>
      <img className="pet-pic" src="https://ibb.co/WK9MKbD" onClick={this.animate} alt="" />
    </div>
  );
 }

  render () {
    return (
    <div>
        {this.state.alive ? this.renderAlivePet() 
          : this.renderDeadPet()
        }
      </div>
    );
  }
}

export default PetPic;
