import React from 'react';
import './login-style.css';
import { Link, withRouter, } from 'react-router-dom';
import { auth } from '../../../firebase';
import * as routes from './constants/routes.js';


const SignIn = () =>
	<div>
		<SignInForm />
	</div>

class SignInForm extends React.Component {
	constructor(props) {
		super(props);
		
	}

	render() {
		return (
  		<div className="container">
			<form className="signUp">
				<h3>Welcome<br />Back to<br />
				Tamagochi !</h3><br />
				<input
				type="email" 
				placeholder="Insert email" 
				autoComplete='off' 
				required
				onChange={this.onEmailChange} />
				<input 
				type="password" 
				placeholder="Insert password" 
				required
				onChange={this.onPasswordChange} />
				<Link to={routes.PASSWORD_FORGET}><p className="p-button" unselectable="on">Forgot your password?</p></Link>
				<Link to={routes.SIGN_UP}><button className="form-btn sx back" type="button">Back</button></Link>
				<Link to={routes.PET}><button className="form-btn dx" type="submit">Log In</button></Link>
			</form>
		</div>
  		);
	}
}

export default SignIn;
