import React from 'react';
import './login-style.css';
import './toggleFunctions.js';
import { showSignIn } from './toggleFunctions.js';

 
class SignUp extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			email: '',
			password: ''
		}
	}

	onEmailChange = (event) => {
		this.setState({email: event.target.value})
	}

	onPasswordChange = (event) => {
		this.setState({password: event.target.value})
	}

	render() {
		const { onRouteChange } = this.props;
		return (
		<div className="container">
			<form className="signUp">
				<h3>Create Your Account</h3>
				<p>Just enter your email address<br />
				and your password for join.
				</p>
				<input 
				className="w100"
				 type="email"
				  placeholder="Insert email" 
				  required autoComplete='off'
				  onChange={onEmailChange} />
				<input type="password" placeholder="Insert password" required />
				<input type="password" placeholder="Verify password" required />
				<button className="form-btn sx log-in" type="button" onClick={ showSignIn }>Log In</button>
				<button className="form-btn dx" type="submit">Sign Up</button>
			</form>
		</div>
		);
	}
}

export default SignUp;
