import React from 'react';
import './dropdown.css';

class Dropdown extends React.Component {
  constructor(props) {
    super();
    
    this.state = {
      showDrop: true,
      value: 'japanese топ',
      category: 'japanese'
    }
    
    this.showDrop = this.showDrop.bind(this);
    /*this.hideDrop = this.hideDrop.bind(this);*/
  }
  
  showDrop(event) {
    event.preventDefault();
    

    this.setState({ showDrop: true }, () => {
      document.addEventListener('click', this.showDrop);
    });
  }


  
  hideDrop(event) {
    this.setState({ showDrop: false }, () => {
      document.removeEventListener('click',     this.hideDrop);
    });
  }

  handleChange = (language) => () => {
    
  }

  changeLang = (language) => {
    console.log(language);
    var lanWithArrow = language + ' топ';
    this.setState({ value: lanWithArrow, showDrop: true });
    this.props.handleLanguageChange(language);
  }

  render () {
    return (
      <div className='dropMenu'>
        <button className='drop-btn' onClick={this.showDrop}>{this.state.value}</button>
      
      {
      this.state.showDrop ? (
        <ul className='dropDown'>
          <li className="elem" onClick={this.handleChange("Japanese")} onClick={this.changeLang("Japanese")}>Japanese</li>
          <li className="elem" onClick={this.handleChange("Korean")} onClick={this.changeLang("Korean")}>Korean</li>
          <li className="elem" onClick={this.handleChange("Polish")} onClick={this.changeLang("Polish")}>Polish</li>
        </ul>
        ) : (
          null
        )
      } 
      </div>
    );
  }
}

export default Dropdown;
