import React from 'react';
import Dropdown from './Dropdown/Dropdown.js';
import './settingscontainer.css';


class SettingsContainer extends React.Component {
  constructor() {
    super();
    
    this.state = {
      languageB: 'Jp',
      languageS: 'Japanese'
    }
  }

  render () {
    return (
          <div id="settingsWrapper">
            <div className="dropdownWrapper">
              <div className="dropdown">
                <div className="settingsTitle">Change word status:</div>
                <div className="dropdownOption">
                 <button className="btn-status">Learning</button>
                 <button className="btn-status">Mastered</button>
                </div>

                <div id="radios">
                  <label for="driving" class="material-icons">
                    <input type="radio" name="mode" id="driving" value="driving" checked/>
                    <span>&#xE531;</span>
                  </label>                
                  <label for="cycling" class="material-icons">
                    <input type="radio" name="mode" id="cycling" value="cycling" />
                    <span>&#xE52F;</span>
                  </label>
                  <label for="walking" class="material-icons">
                    <input type="radio" name="mode" id="walking" value="walking" />
                    <span>&#xE536;</span>
                  </label>
                </div>

              </div>
            </div>
            <div className="dropdownWrapper">
              <div className="dropdown">
                <div className="settingsTitle">Choose category:</div>
                <div className="dropdownOption">
                  <Dropdown />
                </div>
              </div>
            </div>
          </div>
    );
  }
}

export default SettingsContainer;
