import React from 'react';
import Dropdown from './Dropdown/Dropdown.js';
import './settingscontainer.css';


class SettingsContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      addClass: false,
      language: 'Japanese',
      status: 'Learning',
      category: 'animals'
      };
  }

  toggle() {
    this.setState({addClass: !this.state.addClass});
  }

  render () {
    let boxClass = ["status-btn"];
    if(this.state.addClass) {
      boxClass.push('active');
    }
    return (
          <div id="cardSettingsWrapper">
            <div className="statusWrapper">
              <div className="status">
                <div className="statusTitle">Change word status:</div>
                 <div className='status-buttons'>
                 <hr /><hr />
                 <div className={boxClass.join(' ')} onClick={this.toggle.bind(this)}>{this.state.addClass ? "Remove a class" : "Add a class (click the box)"}
                 <br /></div>
                  <button className='status-btn' title='Learning'>
                    &#9998;
                  </button>
                  <button className='status-btn' title='Learnt'>
                    &#x2606;
                  </button>
                  <button className='status-btn' title='Mastered'>
                    &#9812;
                  </button>
                </div>
              </div>
            </div>
            <hr className="hr-class"/>
            <div className="categoryDropdownWrapper">
              <div className="dropdown">
                <div className="settings-dropdown-title">Choose category:</div>
                <div className="categoryDropdownOption">
                  <Dropdown />
                </div>
              </div>
            </div>
          </div>
    );
  }
}

export default SettingsContainer;
