import React, { Component } from 'react';
import FlashCard from './FlashCard/FlashCard.js';
import DrawButton from './DrawButton/DrawButton.js';
import firebase from 'firebase/app';
import 'firebase/database';


import { DB_CONFIG } from './Config/Firebase/db_config';

class CardsContainer extends Component {

	constructor(props){
    super(props);

    this.app = firebase.initializeApp(DB_CONFIG);
    this.database = this.app.database().ref().child('jpn-cards-animals');
    this.updateCard = this.updateCard.bind(this);

    this.state = {
      cards: [],
      currentCard: {},
      value: 'Arial',
      show: false
    }
  }

handleChange = (font) => () => {
    this.setState({ value: font, show: false });
  }
  
  handleToggle = (e) => {
    e.target.focus();
    this.setState({ show: !this.state.show });
  }
  
  handleBlur = (e) => {
    // firefox onBlur issue workaround
    if (e.nativeEvent.explicitOriginalTarget &&
        e.nativeEvent.explicitOriginalTarget === e.nativeEvent.originalTarget) {
      return;
    }

    if (this.state.show) {
      timer = setTimeout(() => {
        this.setState({ show: false });
      }, 200);
    }
  }
  

  componentWillMount(){
    console.log(this.app.database().ref().child('cards'))
    const currentCards = this.state.cards;
    this.database.on('child_added', snap => {
      currentCards.push({
        id: snap.key,
        eng: snap.val().eng,
        kan: snap.val().kan,
        rom: snap.val().rom
      })

      this.setState({
        cards: currentCards,
        currentCard: this.getRandomCard(currentCards)
      })

    })
  }

  getRandomCard(currentCards){
    var randomIndex = Math.floor(Math.random() * currentCards.length);
    var card = currentCards[randomIndex];
    if(card === this.state.currentCard){
      this.getRandomCard(currentCards)
    }

    return(card);
  }

  updateCard(){
    const currentCards = this.state.cards;
    this.setState({
      cards: currentCards,
      currentCard: this.getRandomCard(currentCards)
    })
  }

  render() {
    return (
      <div className="App">
        <div className="cardRow">
          <FlashCard	eng={this.state.currentCard.eng}
            			kan={this.state.currentCard.kan}
            			rom={this.state.currentCard.rom}/>
        </div>
        <div className="buttonRow">
          <DrawButton drawCard={this.updateCard}/>
        </div>
      </div>
    );
  }

}

export default CardsContainer;
