import React, { Component } from 'react';
import QuizCard from './QuizCard.js';
import QuizSettings from './settings/QuizSettings.js';
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css' 

class QuizContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
    	category: 'lalala',
    	quizComplete: false,
    }

    this.receiveCategory = this.receiveCategory.bind(this);
   }

receiveCategory = (cat) => {
	//check if the quiz is complete
	if(!this.state.quizComplete){
		confirmAlert({
        title: 'Confirm to submit',
        message: 'Are you sure you want to restart the quiz?',
        buttons: [
          {
            label: 'Yes',
            onClick: () => {
              var catWithArrow = category + ' топ';
              this.setState({ value: catWithArrow, showDrop: false, category: category });
              this.props.handleCategoryChange(category);
            }
          },
          {
            label: 'No'
          }
        ]
      })
	}
	this.setState({
   		category: cat,
   	});
}

	render() {
	    return (
	    	<div className="centered" id="main-Container">
		        <div className="half purple">
		        	<QuizSettings changeCat={this.receiveCategory} />
		        </div> 
		        <div className="other-half">
		        	<hr />
		        	<QuizCard category={this.state.category} />{this.state.category}
		        </div>
	        </div>
	    );
	}
}

export default QuizContainer;
