import React from 'react';
import ReactCountdownClock  from 'react-countdown-clock';

class PlayCountdown extends React.Component {
	constructor(props){
		super(props);

		this.state={
			restart: this.props.restart,
      completions: 0,

		}
	}

lala = () => {
  if(this.props.restart === false){
    this.setState(
      {
        completions: this.state.completions + 1
      },
      () => console.log('completions', this.state.completions)
    )
  }
}

componentWillMount(){
  if(this.props.restart){
    this.lala();
  }
  else {
    console.log('lala');
  }
}

	render () {
    return (
    	<div>
    		<ReactCountdownClock 
                  key={this.state.completions}
                  seconds={10}
                  color="#ff3a51"
                  alpha={0.9}
                  size={100}
                  paused={this.state.countdownPause}
                  onComplete={this.lala} />
    	</div>
    );
}

}

export default PlayCountdown;
