import React from 'react';
import './playbuttons.css'

class PlayButtons extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      addClass: true,
      addClass2: false,
      addClass3: false
    }
  }
 
  toggle1() {
    if (this.state.addClass === true) {
      //nic
    } else if(this.state.addClass === false){
      this.setState({
        addClass: true,
        addClass2: false,
        addClass3: false
      });
    }
  }
  
  toggle2() {
    if (this.state.addClass2 === true) {
      //nic
    } else if(this.state.addClass2 === false){
      this.setState({
        addClass: false,
        addClass2: true,
        addClass3: false
      });
    }
  }

  toggle3() {
    if (this.state.addClass3 === true) {
      //nic
    } else if(this.state.addClass3 === false){
      this.setState({
        addClass: false,
        addClass2: false,
        addClass3: true
      });
    }
  }

  render () {
    let boxClass = ["status-btn"];
    let boxClass2 = ["status-btn"];
    let boxClass3 = ["status-btn"];
    if(this.state.addClass) {
      boxClass.push('active');
    }
    if(this.state.addClass2) {
      boxClass2.push('active');
    }
    if(this.state.addClass3) {
      boxClass3.push('active');
    }
    return (
      <div className='play-buttons'>
        <button className={boxClass.join(' ')} onClick={this.toggle1.bind(this)} title='Play'>
          ‚öæ
        </button>
        <button className={boxClass2.join(' ')} onClick={this.toggle2.bind(this)} title='Feed'>
          üçå
        </button>
        <button className={boxClass3.join(' ')} onClick={this.toggle3.bind(this)} title='Wash'>
          üí¶
        </button>
        <button className={boxClass3.join(' ')} onClick={this.toggle3.bind(this)} title='Play music'>
          üéπ
        </button>
      </div>
    );
  }
}

export default PlayButtons;
