import React from 'react';
import './wordstatuscheckbox.css';

class WordStatusCheckbox extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      learning: false,
      learnt: false,
      mastered: false
    }
  }

  lala () {
    console.log(this.state.cb1);
    this.setState ({
      cb1: !this.state.cb1
    })
    console.log(cb1);
  }

  render () {
    return (
      <div className="center-on-page">
        <h2>Checkbox</h2>
        <input type="checkbox" name="cb" id="cb1" onClick={this.lala} />
        <label for="cb1">Learning</label>
        <input type="checkbox" name="cb" id="cb2" />
        <label for="cb2">Learnt</label>
        <input type="checkbox" name="cb" id="cb3" />
        <label for="cb3">Mastered</label>
      </div>
    );
  }
}

export default WordStatusCheckbox;
