import React from 'react';
import './quizcard.css';

class QuizCard extends React.Component {
  constructor(props) {
    super(props);
    
    this.state = {
      category: 'animals'
    }
  }

  changeCategory = (cat) => {
    //-------------------------------------------------
    //
    //here send change the viewed words
    //
    //-------------------------------------------------
    this.setState({
      category: cat
    });
  }
 

  render () {
    return (
    <div className="centered" id="main-Container">
        <div className="half purple">
          <div id="title">Choose your quiz category!</div>
          <div id="category-choose">

          </div>
          <div id="title">Choose words' status!</div>
          <div id="category-choose">

          </div>
          <div className="begin-button">
            <button className="begin-btn">Begin</button>
            <div class="button button-4"><p>Hover me!</p></div>
          </div>
        </div>
        <div className="half">
          <div id="tableWrapper">
            {/**/}
          </div>
        </div>
      </div>
    );
  }
}

export default QuizCard;
