import React, { Component } from 'react';
import './quizcard.css';
import Quiz from './../components/Quiz.js';
import Result from './../components/Result.js';
import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/database';
import { db } from '../../../../firebase';
//import { default as fire } from '../../../../firebase';

class QuizCard extends Component {
  constructor(props) {
    super(props);


    if (firebase.apps.length) {

      this.state = {
        counter: 0,
        questionId: 1,
        questions: [],
        ansOpt: [],
        currentAnswers: [],
        currQuestion: '',
        question: '',
        questionTotal: 10,
        answerOptions: [],
        answer: '',
        answersCount: {
          correct: 0,
          incorrect: 0,
        },
        result: '',
        category: '',
        quizDone: false,
        firstSetup: true,

        foodItems: 0,
        playItems: 0,
        washItems: 0,
        musicItems: 0,

        user: null,
        languageIndex: null,
        addedString: null,
      };

      this.handleAnswerSelected = this.handleAnswerSelected.bind(this);
      this.loadDatabase = this.loadDatabase.bind(this);
      this.quizUndone = this.quizUndone.bind(this);
    }
  }

  async getUserData() {
    var user = db.onceGetUser(this.props.uid).then(snapshot => snapshot.val());
    await user.then((value) => {
      this.setState({
        user: value
      })
    });
  }

  async truncCards(cards) {
    var comparedCards = [];
    var userDBCards = db.getUserCards(this.props.uid).then(snapshot => snapshot.val());
    console.log(this.props.uid);
    await userDBCards.then((value) => {
      comparedCards = value;
    });

    var queryString = "";

    switch (this.state.user.language) {
      case 'Korean':
        queryString = "0_";
        break;
      case 'Japanese':
        queryString = "1_";
        break;
      case 'Polish':
        queryString = "2_";
        break;
      default:
        break;
    }

    switch (this.props.category) {
      case 'Animals':
        console.log(this.props.category);
        queryString += "0_";
        break;
      case 'People':
        console.log(this.props.category);
        queryString += "1_";
        break;
      case 'Food':
        console.log(this.props.category);
        queryString += "2_";
        break;
      case 'School':
        console.log(this.props.category);
        queryString += "3_";
        break;
      case 'House':
        console.log(this.props.category);
        queryString += "4_";
        break;
      default:
        console.log("null");
        break;
    }

    var newList = [];
    console.log(queryString);
    console.log(cards);
    console.log(comparedCards);
    for (var i = 0; i < cards.length; ++i) {
      for (var j = 0; j < comparedCards.length; ++j) {
        if (comparedCards[j].id === (queryString + i) && comparedCards[j].status !== 'Mastered') {
          newList.push(cards[i]);
        }
      }
    }
    return newList;
  }


  async loadDatabase() {

    //console.log(this.props.generateNew);
    if (this.props.generateNew === true) {

