import React from 'react';
import './progresscard.css';

class ProgressCard extends React.Component {
  constructor(props) {
    super(props);
    
    this.state = {
      category: 'animals'
    }
  }

  changeCategory = (lang) => {
    //-------------------------------------------------
    //
    //here send change the viewed words
    //
    //-------------------------------------------------
    if(lang==='Japanese'){
      this.setState({
        languageB: 'Jp',
        languageS: lang
      });
    } else if(lang==='Korean'){
      this.setState({
        languageB: 'Kr',
        languageS: lang
      });
    } else if(lang==='Polish'){
      this.setState({
        languageB: 'Pl',
        languageS: lang
      });
    }
  }
 

  render () {
    return (
    <div className="centered" id="mainContainer">
        <div className="half purple">
          <div id="title">Tamago settings</div>
          <div id="preview">
            <div id="previewText">{this.state.languageB}</div>
            <div id="previewTitle"><span>{this.state.languageS}</span> Language</div>
          </div>
          <div id="switchWrapper">
            <div id="switchOuter">
              {/*<div id="switchInner"></div>*/}
            </div>
          </div>
        </div>
        <div className="half">
          <div id="settingsWrapper">
            <div className="dropdownWrapper">
              <div className="dropdown">
                
              </div>
            </div>
            <div id="colorPicker">
              <div className="dropdownTitle">Language</div>
              <div id="colorsWrapper">
                {/*<Dropdown handleLanguageChange={this.changeCategory}/>*/}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ProgressCard;
