import React from 'react';
import Dropdown from './Dropdown/Dropdown.js';
import './settingscontainer.css';


class SettingsContainer extends React.Component {
  constructor() {
    super();
    
    this.state = {
      languageB: 'Jp',
      languageS: 'Japanese'
    }
  }

  render () {
    return (
          <div id="settingsWrapper">
            <div className="dropdownWrapper">
              <div className="dropdown">
                <div className="settingsTitle">Change word status:</div>
                <div className="dropdownOption">
                 <button className="btn-status">Learning</button>
                 <button className="btn-status">Mastered</button>
                 
                 <div class='status-buttons'>
                    <a class='active' href='#' title='Title 1'>
                      &#x2606;
                    </a>
                    <a class='active' href='#' title='Title 2'>
                      &#x262F;
                    </a>
                    <a href='#' title='Title 3'>
                      &#x2666;
                    </a>
                    <a href='#' title='Title 4'>
                      &#x267A;
                    </a>
                  </div>

                </div>
              </div>
            </div>
            <div className="dropdownWrapper">
              <div className="dropdown">
                <div className="settingsTitle">Choose category:</div>
                <div className="dropdownOption">
                  <Dropdown />
                </div>
              </div>
            </div>
          </div>
    );
  }
}

export default SettingsContainer;
